mapper.select:
SELECT status_code as keyWordStatusCode
  FROM ADMIN.TREND_DATA
 WHERE TREND_ID=1 ;

SELECT status_code as productStatusCode
  FROM ADMIN.SSADAGU_CONTENT
 WHERE CONTENT_ID =1;

SELECT status_code as aIContentStatusCode
  FROM ADMIN.AI_GENERATED_CONTENT
 WHERE AI_CONTENT_ID=1;

SELECT status_code as publishStatusCode
  FROM ADMIN.BLOG_PUBLISH
 WHERE PUBLISH_ID =1 ;
